import {React, useEffect, useState} from "react";

function Message({dispatch}) {
    const [isVisible,setIsVisible] = useState(false);

        // Run the effect whenever the prop changes
    useEffect(() => {
        // Set visibility to false to reset the component
        setIsVisible(false);

        // Trigger fade-in after a small delay
        const timeout = setTimeout(() => {
        setIsVisible(true);
        }, 0);

        // Clean up by resetting visibility and clearing the timeout
        return () => {
        setIsVisible(false);
        clearTimeout(timeout);
        };
    }, [dispatch]); // Dependency array: Runs on prop change

    return (
        <div className={`mt-[3rem] transition-opacity duration-1000 ease-in ${isVisible ? 'opacity-100' : 'opacity-0'}`}>
        <h2 className="text-[#0094C2] text-center text-xl md:text-3xl font-lato">
            {dispatch}
        </h2>
        </div>
    )
}

export default Message;
