import {React, useEffect, useState} from "react";

function Message({dispatch}) {
    const [isVisible,setIsVisible] = useState(false);

    useEffect(() => {
        // Set visibility to true on every render with a small delay
        setIsVisible(true)

        // // Clean up function to reset visibility before the next render
        return () => {
          setIsVisible(false);
        };
      });

    return (
        <div className={`mt-[3rem] transition-opacity duration-1000 ease-in ${isVisible ? 'opacity-100':'opacity-0'}`}>
        <h2 className="text-[#0094C2] text-center text-xl md:text-3xl font-lato">
            {dispatch}
        </h2>
        </div>
    )
}

export default Message;
