import {React, useEffect, useState} from "react";

function Message({dispatch}) {
    const [isVisible,setIsVisible] = useState(false);

    useEffect(() => {
        // Set isVisible to false initially to hide the element
        setIsVisible(false);

        // Set it to true after a short delay to trigger the transition
        const timeout = setTimeout(() => {
          setIsVisible(true);
        }, 10); // You can adjust the delay if necessary

        return () => clearTimeout(timeout); // Cleanup timeout
      });

    return (
        <div className={`mt-[3rem] transition-opacity duration-1000 ease-in ${isVisible ? 'opacity-100':'opacity-0'}`}>
        <h2 className="text-[#0094C2] text-center text-xl md:text-3xl font-lato">
            {dispatch}
        </h2>
        </div>
    )
}

export default Message;
