import React, {useState} from 'react';
import Clock from './Clock';
import DeviceGroup from './DeviceGroup';
import {DndContext} from '@dnd-kit/core';
import DndIcon from './DnDIcon';

function generatePositions(numGroups) {
    let radius = 10;
    let positions = [];
    if (numGroups == 1) {
        positions.push([["1/2","1/2"]]);
    } else {
        let theta = 2*Math.PI/numGroups;
        for (let i = 0; i < numGroups; ++i) {
            positions.push([(Math.round(radius*Math.cos(i*theta)) + 15).toString(),
                (Math.round(radius*Math.sin(i*theta)) + 15).toString()]);
        }
    }
    return positions;
}

function Crown() {
    //const [stopClock,setStopClock] = useState(false);

