import React, {useState} from 'react';
import Clock from './Clock';
import DeviceGroup from './DeviceGroup';
import {DndContext, useDndMonitor} from '@dnd-kit/core';
import DndIcon from './DnDIcon';


function generatePositions(numGroups) {
    let radius = 13;
    let positions = [];
    if (numGroups === 1) {
        positions.push([["1/2","1/2"]]);
    } else {
        let theta = 2*Math.PI/numGroups;
        for (let i = 0; i < numGroups; ++i) {
            positions.push([(Math.round(radius*Math.cos(i*theta)) + 17.5-4).toString(),
                (17.5-4-Math.round(radius*Math.sin(i*theta))).toString()]);
        }
    }
    return positions;
}

function handleDragMove(event) {
    console.log('Drag move event triggered');  
}

function handleDragStart(event) {
    console.log('Drag start event triggered');
}

function Crown() {
    //const [stopClock,setStopClock] = useState(false);

    const [deviceGroups, setDeviceGroups] = useState([
        {id:'A',color:'bg-green-500',devices:[{id:0, name:'skynet', type:'A'}],bubble:false},
        {id:'B',color:'bg-green-500',devices:[{id:1, name:'sky',type:'B'}],bubble:false},
        {id:'C',color:'bg-green-500',devices:[{id:2, name:'skys',type:'C'}],bubble:false},
        {id:'D',color:'bg-green-500',devices:[{id:3, name:'skynets', type:'D'}],bubble:false}
    ]);


    const addDeviceGroup = () => {
        setDeviceGroups([...deviceGroups, [{id:1, name:'sky',type:'E'}]]);
    };

    const removeDeviceGroup = () => {

    };

    let positions = generatePositions(deviceGroups.length);

    function handleDragOver(event) {
        console.log('Drag over event triggered');
        console.log(event);
        if (event.over != null && event.over.id !== event.active.id) {
            setDeviceGroups(deviceGroups.map((group) => group.id === event.over.id ? {...group, color:'bg-rose-500',bubble:true} : group));
        } else {
            setDeviceGroups(deviceGroups.map((group) => (group.devices.length > 1) ? {...group, color:'bg-green-500',bubble:true} : {...group, color:'bg-green-500',bubble:false}));
        }
    }

    function handleDragEnd(event) {

    }

    function Monitor() {
        useDndMonitor({
            onDragOver(event) {handleDragOver(event)},
            onDragEnd(event) {handleDragEnd(event)}
        })
    }


    return (
        <div className='flex flex-col items-center w-full min-w-full max-w-full overflow-x-clip mt-5'>
        <div className='relative w-[90vmin] h-[90vmin] md:w-[35rem] md:h-[35rem]'>
            <Clock/>
            <DndContext>
            <Monitor/>
            {deviceGroups.map((group,idx) => {
                return (
                <DndIcon iconId={group.id} key={idx} left={positions[idx][0]} top={positions[idx][1]} bubble={group.bubble}>
                <DeviceGroup devices={group.devices} color={groupColors[idx]}/>
                </DndIcon>
                )
            })}
            </DndContext>   
        </div>
        </div>
    )

    function handleDragEnd(event) {
        console.log('Drag end event triggered');
        console.log(event.active);
        console.log(event.over);
    }
}

export default Crown;
