import React, { useState } from 'react';
import { useSpring, animated} from 'react-spring';
import * as flubber from 'flubber';

const ArrowToCircle = () => {
  const [toggle, setToggle] = useState(false);

  const syncPath = "M14.3935 5.37371C18.0253 6.70569 19.8979 10.7522 18.5761 14.4118C17.6363 17.0135 15.335 18.7193 12.778 19.0094M12.778 19.0094L13.8253 17.2553M12.778 19.0094L14.4889 20";
  const circlePath = "M12 2C6.477 2 2 6.477 2 12s4.477 10 10 10 10-4.477 10-10S17.523 2 12 2z";
  const heart = "M12 2C73 20 20 74 20 140c0 135 136 170 228 303 88-132 229-173 229-303 0-66-54-120-120-120-48 0-90 28-109 69-19-41-60-69-108-69z";
  const interpolator = flubber.fromCircle(12,12,10,heart);

  const {t} = useSpring({
    t: toggle ? 1 : 0,
    config: { duration: 1000 },
  });

  return (
    <div
      className="absolute top-1/2 left-1/2"
      style={{ width: '40rem', height: '40rem', cursor: 'pointer' }}
      onClick={() => setToggle(!toggle)}
    >
      <animated.svg
        viewBox="0 0 24 24"
        fill="red"
        xmlns="http://www.w3.org/2000/svg"
        width="100%"
        height="100%"
      >
        <animated.path
          d={t.to(interpolator)}
          stroke="#FEBF55"
          strokeLinecap="round"
          strokeLinejoin="round"
        />
      </animated.svg>
    </div>
  );
};

export default ArrowToCircle;
