    return (
        <div className='relative h-[30rem] mt-10 bg-amber-400'>
            <DndContext>
            {deviceGroups.map((group,index) => {
                return (<Droppable key={index}>
                <Draggable> 
                <DeviceGroup devices={group}/>
                </Draggable>
                </Droppable>);
            })}
            </DndContext>   

        </div>
    )
}

export default Crown;
