import React, { useState } from 'react';
import { useSpring, animated } from 'react-spring';

const ArrowToCircle = () => {
  const [toggle, setToggle] = useState(false);

  const animationProps = useSpring({
    d: toggle
      ? "M12 2C6.477 2 2 6.477 2 12s4.477 10 10 10 10-4.477 10-10S17.523 2 12 2z" // Circle path
      : "M14.3935 5.37371C18.0253 6.70569 19.8979 10.7522 18.5761 14.4118C17.6363 17.0135 15.335 18.7193 12.778 19.0094M12.778 19.0094L13.8253 17.2553M12.778 19.0094L14.4889 20M9.60651 18.6263C5.97465 17.2943 4.10205 13.2478 5.42394 9.58823C6.36371 6.98651 8.66504 5.28075 11.222 4.99059M11.222 4.99059L10.1747 6.74471M11.222 4.99059L9.51114 4", // Original sync symbol path
    config: { duration: 1000 }
  });

  return (
    <div
      className="absolute top-1/2 left-1/2"
      style={{ width: '40rem', height: '40rem', cursor: 'pointer' }}
      onClick={() => setToggle(!toggle)}
    >
      <animated.svg
        viewBox="0 0 24 24"
        fill="none"
        xmlns="http://www.w3.org/2000/svg"
        width="100%"
        height="100%"
      >
        <animated.path
          d={animationProps.d}
          stroke="#FEBF55"
          strokeLinecap="round"
          strokeLinejoin="round"
        />
      </animated.svg>
    </div>
  );
};

export default ArrowToCircle;
